// src/components/Main/Main.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Main.css';

const Main = ({ selectedSport }) => {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/fixtures/${selectedSport}`);
        setMatches(response.data);
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      }
    };

    if (selectedSport) {
      fetchMatches();
    }
  }, [selectedSport]);

  return (
    <div className="main">
      {error && <p className="error">{error}</p>}
      <ul>
        {matches.map((match) => (
          <li key={match.matchId}>{match.team1} vs {match.team2} on {match.matchDate}</li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
