import React, { useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import "./App.css";

const App = () => {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState(null);

  const fetchMatches = async (sport) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/${sport}/fixtures`
      );
      setMatches(response.data);
    } catch (err) {
      setError("Failed to fetch matches");
    }
  };

  const handleSportChange = (sport) => {
    fetchMatches(sport);
  };

  return (
    <div className="main">
      <Navbar onSportChange={handleSportChange} />
      {error && <p className="error">{error}</p>}
      <div className="matches-list">
        {matches.map((match) => (
          <div key={match.match_id} className="match-card">
            <h3>
              {match.team1} vs {match.team2}
            </h3>
            <p>Date: {match.match_date}</p>
            <p>Type: {match.match_type}</p>
            {match.score && <p>Score: {match.score}</p>}
            {match.set_scores && <p>Set Scores: {match.set_scores}</p>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
