// src/components/FixtureDetails/FixtureDetails.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function FixtureDetails() {
  const { sport, matchId } = useParams();
  const [fixture, setFixture] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFixtureDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/fixtures/${sport}/${matchId}`);
        setFixture(response.data);
        setLoading(false);
      } catch (error) {
        console.error("There was an error fetching the fixture details!", error);
        setLoading(false);
      }
    };

    fetchFixtureDetails();
  }, [sport, matchId]);

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      {fixture ? (
        <div>
          <h1>Fixture Details</h1>
          <p>Match ID: {fixture.matchId}</p>
          <p>Team 1: {fixture.team1}</p>
          <p>Team 2: {fixture.team2}</p>
          <p>Match Date: {fixture.matchDate}</p>
          <p>Match Status: {fixture.matchStatus}</p>
          <p>Score: {fixture.team1score} - {fixture.team2score}</p>
        </div>
      ) : (
        <p>Fixture not found.</p>
      )}
    </div>
  );
}

export default FixtureDetails;
