import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Main.css';

function Main({ selectedSport }) {
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/fixtures/${selectedSport}`)
      .then(response => setFixtures(response.data))
      .catch(error => console.error('Error fetching data:', error));
  }, [selectedSport]);

  const sortedFixtures = [...fixtures].sort((a, b) => new Date(b.matchDate) - new Date(a.matchDate));

  return (
    <div className="main">
      {sortedFixtures.length > 0 ? (
        sortedFixtures.slice(0, 3).map((fixture, index) => (
          <Link key={index} to={`/fixtures/${fixture.matchId}`} className="fixture-link">
            <div className="fixture-card">
              <h3>{fixture.team1} vs {fixture.team2}</h3>
              <p>Date: {fixture.matchDate}</p>
              <p>Type: {fixture.matchType}</p>
              <p>{fixture.team1} Score: {fixture.team1Score}</p>
              <p>{fixture.team2} Score: {fixture.team2Score}</p>
            </div>
          </Link>
        ))
      ) : (
        <p>No fixtures available</p>
      )}
    </div>
  );
}

export default Main;
