// src/components/Main/Main.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './Main.css';

const Main = ({ selectedSport }) => {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState(null);

  const fetchMatches = async (sport) => {
    try {
      const response = await axios.get(`http://localhost:8080/fixtures/${sport}`);
      setMatches(response.data);
    } catch (err) {
      setError('Failed to fetch data');
      console.error(err);
    }
  };

  React.useEffect(() => {
    if (selectedSport) {
      fetchMatches(selectedSport);
    }
  }, [selectedSport]);

  return (
    <div className="main">
      {error && <p className="error">{error}</p>}
      <ul>
        {matches.map(match => (
          <li key={match.matchId}>{match.team1} vs {match.team2} on {match.matchDate}</li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
