// src/components/FixtureDetails.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FixtureDetails = ({ sport }) => {
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    const fetchFixtures = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/fixtures/${sport}`);
        setFixtures(response.data);
      } catch (error) {
        console.error('Error fetching fixtures:', error);
      }
    };

    fetchFixtures();
  }, [sport]);

  return (
    <div>
      <h2>{sport} Fixtures</h2>
      <ul>
        {fixtures.map((fixture) => (
          <li key={fixture.matchId}>
            <h3>Match between {fixture.team1} and {fixture.team2}</h3>
            <p>Date: {fixture.matchDate}</p>
            <p>Type: {fixture.matchType}</p>
            <p>{fixture.team1} Score: {fixture.team1Score}</p>
            <p>{fixture.team2} Score: {fixture.team2Score}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FixtureDetails;
