import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './FixtureDetails.css';

function FixtureDetails() {
  const { id } = useParams(); // Get the fixture ID from the URL
  const [fixture, setFixture] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/fixtures/${id}`)
      .then(response => setFixture(response.data))
      .catch(error => console.error('Error fetching fixture details:', error));
  }, [id]);

  if (!fixture) {
    return <div>Loading...</div>;
  }

  return (
    <div className="fixture-details">
      <h2>{fixture.team1} vs {fixture.team2}</h2>
      <p>Date: {fixture.matchDate}</p>
      <p>Type: {fixture.matchType}</p>
      <p>{fixture.team1} Score: {fixture.team1Score}</p>
      <p>{fixture.team2} Score: {fixture.team2Score}</p>
      <p>Location: {fixture.location}</p>
      <p>Referee: {fixture.referee}</p>
      {/* Add more details as needed */}
    </div>
  );
}

export default FixtureDetails;
