import React, { useState } from 'react';
import './Main.css';

const Main = () => {
    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchMatches = (sport) => {
        setLoading(true);
        setError(null);

        const apiUrl = `/api/${sport.toLowerCase()}-fixtures`; // Adjust the endpoint as needed
        
        fetch(apiUrl)
            .then(response => response.json())
            .then(data => {
                setMatches(data);
                setLoading(false);
            })
            .catch(err => {
                setError('Error fetching data.');
                setLoading(false);
            });
    };

    return (
        <div className="main">
            <div className="main-buttons">
                <button onClick={() => fetchMatches('cricket')}>Cricket</button>
                <button onClick={() => fetchMatches('basketball')}>Basketball</button>
                <button onClick={() => fetchMatches('football')}>Football</button>
                <button onClick={() => fetchMatches('volleyball')}>Volleyball</button>
            </div>
            {loading && <p>Loading...</p>}
            {error && <p className="error">{error}</p>}
            <div className="matches-list">
                {matches.map((match) => (
                    <div key={match.matchId} className="match-card">
                        <h3>{match.team1} vs {match.team2}</h3>
                        <p>Date: {match.matchDate}</p>
                        <p>Innings 1 Score: {match.innings1Score}</p>
                        <p>Innings 2 Score: {match.innings2Score}</p>
                        <p>Match Type: {match.matchType}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Main;
