// src/components/Main/Main.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function Main({ selectedSport }) {
  const [fixtures, setFixtures] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFixtures = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/fixtures/${selectedSport}`);
        setFixtures(response.data);
        setLoading(false);
      } catch (error) {
        console.error("There was an error fetching the fixtures!", error);
        setLoading(false);
      }
    };

    fetchFixtures();
  }, [selectedSport]);

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h1>{selectedSport} Fixtures</h1>
      <ul>
        {fixtures.map(fixture => (
          <li key={fixture.matchId}>
            <a href={`/${selectedSport}/${fixture.matchId}`}>
              {fixture.team1} vs {fixture.team2} on {fixture.matchDate}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Main;
