import React, { useEffect, useState } from 'react';
import './Main.css';

const Main = ({ selectedSport }) => {
    const [matches, setMatches] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:8080/api/fixtures/${selectedSport}`)
            .then(response => response.json())
            .then(data => setMatches(data))
            .catch(error => console.error('Error fetching data:', error));
    }, [selectedSport]);

    return (
        <div className="main">
            <h2>Matches for {selectedSport}</h2>
            <ul>
                {matches.map(match => (
                    <li key={match.matchId}>{match.team1} vs {match.team2} on {match.matchDate}</li>
                ))}
            </ul>
        </div>
    );
};

export default Main;
