// src/App.js
import React, { useState } from "react";
import FixtureDetails from "./components/FixtureDetails";
import "./App.css";

function App() {
  const [selectedSport, setSelectedSport] = useState("");

  const handleSportClick = (sport) => {
    setSelectedSport(sport);
  };

  return (
    <div className="App">
      <nav>
        <button onClick={() => handleSportClick("cricket")}>Cricket</button>
        <button onClick={() => handleSportClick("football")}>Football</button>
        <button onClick={() => handleSportClick("volleyball")}>
          Volleyball
        </button>
        <button onClick={() => handleSportClick("basketball")}>
          Basketball
        </button>
      </nav>
      <main>{selectedSport && <FixtureDetails sport={selectedSport} />}</main>
    </div>
  );
}

export default App;
