import React from 'react';
import axios from 'axios';
import './Navbar.css';

const Navbar = () => {
    const handleButtonClick = (sport) => {
        if (sport === 'Cricket') {
            axios.get('/api/cricket-fixtures')
                .then(response => {
                    console.log('Cricket Matches:', response.data);
                    // Handle displaying the matches here
                })
                .catch(error => {
                    console.error('There was an error fetching the cricket matches!', error);
                });
        }
        // You can add similar functionality for other sports here
    };

    return (
        <div className="navbar">
            <h1 className="navbar-title">Sports Connect</h1>
            <div className="navbar-buttons">
                <button onClick={() => handleButtonClick('Cricket')}>Cricket</button>
                <button onClick={() => handleButtonClick('Basketball')}>Basketball</button>
                <button onClick={() => handleButtonClick('Football')}>Football</button>
                <button onClick={() => handleButtonClick('Volleyball')}>Volleyball</button>
            </div>
        </div>
    );
};

export default Navbar;
